    Linux(:i686, libc=:glibc) => ("$bin_prefix/GDAL.v3.0.2.i686-linux-gnu.tar.gz", "4c914accb6ee2176d4b8c968dbe49414808b7e49b649bbfaec8e83dcaaa78070"),
